    /**
     * Operation addBank
     *
     * Add a new bank.
     *
     *
     * @return Http response
     */
    public function addBank()
    {
        $form = Request::only(
            'bank_name',
            'swift_code',
            'bank_code'
            );

        $bank = new Bank;

            $bank->bank_name                   =         $form['bank_name'];
            $bank->swift_code                  =         $form['swift_code'];
            $bank->bank_code                   =         $form['bank_code'];

        if($bank->save()) {

            return Response()->json(array('msg' => 'Success: bank added','bank' => $bank), 200);
        }
    }
    




















    /**
