     *
     * @return Http response
     */
    public function deleteBank($bank_id)
    {
        $input = Request::all();


        $deleted = Bank::destroy($bank_id);

        if($deleted){
            return response()->json(['msg'=>"bank deleted"], 200,array(),JSON_PRETTY_PRINT);
        }else{
