    /**
     * Operation addBudgetItem
     *
     * Add a new budget_item.
     *
     *
     * @return Http response
     */
    public function addBudgetItem(HttpRequest $httpRequest)
    {
        try{
            $budget_objective = new BudgetObjective();
            $budget_objective->budget_id = $httpRequest->budget_id;
            $budget_objective->objective_id = $httpRequest->objective_id;
            if ($httpRequest->objective_id == '-1') {
                $budget = Budget::find($httpRequest->budget_id);
                $budget_objective->objective_name = (!empty($budget->project) ? ($budget->project->project_code ?? '').' '.($budget->project->project_name ?? '') : ''). ' Objective 1';
            }
            $budget_objective->disableLogging();
            $budget_objective->save();
    
            $accounts = $httpRequest->accounts;
            foreach($accounts as $account){
                $budget_account = new BudgetAccount();
                $budget_account->budget_objective_id = $budget_objective->id;
                $budget_account->account_id = $account['account_id'];
                $budget_account->amount = $account['amount'];
                $budget_account->created_by_id = $this->current_user()->id;
                $budget_account->disableLogging();
                $budget_account->save();
            }

            return Response()->json(['msg' => 'Success: budget_item added','budget_objective' => $budget_objective], 200);
        }
        catch(Exception $e){
            return Response()->json(['error' => 'Something went wrong','msg' => $e->getMessage()], 500);
        }
    }
    






















    /**
     * Operation updateBudgetItem
     *
     * Update an existing budget_item.
     *
     *
     * @return Http response
     */
    public function updateBudgetItem(HttpRequest $httpRequest)
    {
        try{
            $budget_objective = BudgetObjective::findOrFail($httpRequest->id);
            $budget_objective->objective_id = $httpRequest->objective_id;
            $budget_objective->disableLogging();
            $budget_objective->save();
    
            $accounts = $httpRequest->accounts;
            $new_accounts = [];
            $old_accounts = BudgetAccount::where('budget_objective_id', $budget_objective->id)->pluck('id')->toArray();
            foreach($accounts as $account){
                if(empty($account['id'])) {
                    $budget_account = new BudgetAccount();
                }
                else {
                    $budget_account = BudgetAccount::find($account['id']);
                }
                $budget_account->budget_objective_id = $budget_objective->id;
                $budget_account->account_id = $account['account_id'];
                $budget_account->amount = $account['amount'];
                $budget_account->disableLogging();
                $budget_account->save();
                $new_accounts[] = $budget_account->id;
            }
            foreach($old_accounts as $old_account){
                if(!in_array($old_account, $new_accounts)){
                    BudgetAccount::destroy($old_account);
                }
            }

            return Response()->json(['msg' => 'Success','budget_objective' => $budget_objective], 200);
        }
        catch(Exception $e){
            return Response()->json(['error' => 'Something went wrong','msg' => $e->getMessage()], 500);
        }
    }
    






















    /**
     * Operation deleteBudgetItem
     *
     * Deletes an budget_item.
     *
     * @param int $budget_item_id budget_item id to delete (required)
     *
     * @return Http response
     */
    public function deleteBudgetItem($id)
    {
        try{
            $budget_objective = BudgetObjective::findOrFail($id);
            foreach($budget_objective->accounts as $account){
                BudgetAccount::destroy($account->id);
            }
            $budget_objective->delete();
            return response()->json(['msg'=>"budget objective deleted"], 200);
        }        
        catch(Exception $e){
            return response()->json(['error'=>"Something went wrong"], 500);
        }
    }
    






















    /**
     * Operation getBudgetItemById
     *
     * Find budget_item by ID.
     *
     * @param int $budget_item_id ID of budget_item to return object (required)
     *
     * @return Http response
     */
    public function getBudgetItemById($budget_item_id)
    {
        try{
            $response   = BudgetItem::with('objective')->findOrFail($budget_item_id);           
            return response()->json($response, 200,array(),JSON_PRETTY_PRINT);

        }catch(Exception $e){
            $response =  ["error"=>"budget_item could not be found"];
            return response()->json($response, 404,array(),JSON_PRETTY_PRINT);
        }
    }
    






















