    /**
     * Operation addCountry
     *
     * Add a new country.
     *
     *
     * @return Http response
     */
    public function addCountry()
    {
        $form = Request::only(
            'country_name',
            'abbrv'
            );

        $country = new Country;

            $country->country_name                   =         $form['country_name'];
            $country->abbrv                          =         $form['abbrv'];

        if($country->save()) {

            return Response()->json(array('msg' => 'Success: country added','country' => $country), 200);
        }
    }
    


















    /**
     * Operation updateCountry
     *
     * Update an existing country.
     *
     *
     * @return Http response
     */
    public function updateCountry()
    {
        $form = Request::only(
            'id',
            'country_name',
            'abbrv'
            );

        $country = Country::find($form['id']);

            $country->country_name            =         $form['country_name'];
            $country->abbrv                   =         $form['abbrv'];

        if($country->save()) {

            return Response()->json(array('msg' => 'Success: country updated','country' => $country), 200);
        }
    }
    


















    /**
     * Operation deleteCountry
     *
     * Deletes an country.
     *
     * @param int $country_id country id to delete (required)
     *
     * @return Http response
     */
    public function deleteCountry($country_id)
    {
        $input = Request::all();


        $deleted = Country::destroy($country_id);

        if($deleted){
            return response()->json(['msg'=>"country deleted"], 200,array(),JSON_PRETTY_PRINT);
        }else{
            return response()->json(['error'=>"country not found"], 404,array(),JSON_PRETTY_PRINT);
        }
    }
    


















    /**
     * Operation getCountryById
     *
     * Find country by ID.
     *
     * @param int $country_id ID of country to return object (required)
     *
     * @return Http response
     */
    public function getCountryById($country_id)
    {
        $input = Request::all();

        try{

            $response   = Country::findOrFail($country_id);
           
            return response()->json($response, 200,array(),JSON_PRETTY_PRINT);

        }catch(Exception $e){

            $response =  ["error"=>"country could not be found"];
            return response()->json($response, 404,array(),JSON_PRETTY_PRINT);
        }
    }



















