                                            $item->status_id = 4;
                                            $item->disableLogging();
                                            $item->save();
                                        }
                                    }                                                                        
                                }
                                $invoice->approver_id = $lpo->approver_id;
                            }
                            $invoice->requisition_id = $lpo->requisition_id;
                            $invoice->ref = $requisition->ref.'-INV-'.$this->pad_with_zeros(2, $invoice->getNextRefNumber());
