    /**
     * Operation mobilePaymentsGet
     *
     * mobile_payments List.
     *
     *
     * @return Http response
     */
    public function mobilePaymentsGet()
    {
        $input = Request::all();
        //query builder
        $qb = MobilePayment::query();
        $qb = $qb->where(function($query){
            $query->whereNull('archived')->orWhere('archived', '!=', 1);
        });
        if(!array_key_exists('lean', $input)){
            $qb = MobilePayment::with(['requested_by','requested_action_by','mobile_payment_type',
                                        'status','project_manager','county','currency','rejected_by',
                                        'payees_upload_mode','payees','approvals','allocations','program_activity']);
        }

        $total_records = $qb->count();
        $records_filtered = 0;

        // Sent to bank, awaiting reconciliation
        if(array_key_exists('bank_approvable', $input) && $input['bank_approvable']==true){
            $qb = $qb->whereIn('status_id', ['4','13'])->orderBy('created_at', 'desc');
        }

        // Corrected, awaiting accountant approval
        if(array_key_exists('corrected_approvable', $input) && $input['corrected_approvable']==true){
            $qb = $qb->where('status_id', '12');
        }

        // Approved, awaiting payment
        if(array_key_exists('awaiting_payment', $input) && $input['awaiting_payment']==true){
            $qb = $qb->where('status_id', '17');
        }

        //if status is set
        if(array_key_exists('status', $input)){
            $status_ = (int) $input['status'];

            if($status_ >-1){
                $qb = $qb->where('status_id', $input['status'])
                        ->where('requested_by_id',$this->current_user()->id);
            }elseif ($status_==-1) {
                $qb =  $qb->where('requested_by_id',$this->current_user()->id);
            }elseif ($status_==-2) {
                
            }elseif ($status_==-3) {
                $qb = $qb->where('project_manager_id',$this->current_user()->id);
            }
        }

        $app_stat = $this->approvable_statuses ;
        //if approvable is set

        if(array_key_exists('approvable', $input)){
            $qb = $qb->where(function ($query) use ($app_stat) {                    
                foreach ($app_stat as $key => $value) {
                    $query->orWhere('status_id',$value['id']);
                }
            });
        }

        if(array_key_exists('my_approvables', $input)){
            $current_user = $this->current_user();
            if($current_user->hasRole([
                'super-admin',
                'admin',
                'director',
                'associate-director',
                'financial-controller',
                'program-manager', 
                'accountant', 
                'assistant-accountant',
                'financial-reviewer']
            )){                   
                $qb = $qb->where(function ($query) use ($app_stat,$current_user) {
                    foreach ($app_stat as $key => $value) {
                        $permission = 'APPROVE_MOBILE_PAYMENT_'.$value['id'];
                        if($current_user->can($permission)&&$value['id']==2){
                            $query->orWhere(function ($query1) use ($value,$current_user) {

                                $query1->where(function ($query1) use ($value,$current_user) {
                                    $query1->where('status_id',$value['id']);
                                    $query1->where('project_manager_id',$current_user->id)
                                            ->whereNull('approver_id');
                                });
                                $query1->orWhere(function ($query1) use ($value,$current_user) {
                                    $query1->where('status_id',$value['id']);
                                    $query1->where('approver_id',$current_user->id);
                                });
                            });
                        }
                        else if($current_user->can($permission)){
                            $query->orWhere('status_id',$value['id']); 
                        }
                    }
                });
            }
        }

        //searching
        if(array_key_exists('searchval', $input)){
            $qb = $qb->where(function ($query) use ($input) {                
                $query->orWhere('ref','like', '%' . $input['search']['value']. '%');
                $query->orWhere('expense_desc','like', '%' . $input['searchval']. '%');
                $query->orWhere('expense_purpose','like', '%' . $input['searchval']. '%');
            });

            $records_filtered = $qb->count();
        }

        //ordering
        if(array_key_exists('order_by', $input)&&$input['order_by']!=''){
            $order_direction     = "asc";
            $order_column_name   = $input['order_by'];
            if(array_key_exists('order_dir', $input)&&$input['order_dir']!=''){                
                $order_direction = $input['order_dir'];
            }
            $qb = $qb->orderBy($order_column_name, $order_direction);
        }

        //limit
        if(array_key_exists('limit', $input)){
            $qb = $qb->limit($input['limit']);
        }

        if(array_key_exists('datatables', $input)){
            //searching
            if(!empty($input['search']['value'])){
                $qb = $qb->where(function ($query) use ($input) {                
                    $query->where('ref','like', '%' . $input['search']['value']. '%')
                        ->orWhere('expense_desc','like', '%' . $input['search']['value']. '%')
                        ->orWhere('expense_purpose','like', '%' . $input['search']['value']. '%')
                        ->orWhereHas('requested_by', function ($query) use ($input) {
                            $query->where('f_name','like', '%' . $input['search']['value']. '%')
                                ->orWhere('l_name','like', '%' . $input['search']['value']. '%');
                        });
                });
            }

            foreach($input['columns'] as $column){
                if(!empty($column['search']['value']) && !empty($column['name'])){

                    if($column['name'] == 'requested_by' || $column['name'] == 'project_manager'){
                        $qb = $qb->where(function ($query) use ($column) {                
                            $query->whereHas($column['name'], function ($query) use ($column) {
                                $query->where('f_name','like','%' .$column['search']['value']. '%');
                                $query->orWhere('l_name','like','%' .$column['search']['value']. '%');
                            });
                        });
                    }
                    else if($column['name'] == 'amount'){
                        $qb = $qb->get()->where('totals', 'like', '%' .$column['search']['value']. '%');
                    }
                    else {
                        $qb = $qb->where(function ($query) use ($column) {                
                            $like = (!empty($column['filter']) && $column['filter'] == 'absolute') ? '' : '%';        
                            $query->where($column['name'],'like', $like . $column['search']['value']. $like);
                        });
                    }
                    
                }
            }

            $records_filtered = $qb->count();

            //ordering
            $order_column_id    = (int) $input['order'][0]['column'];
            $order_column_name  = $input['columns'][$order_column_id]['order_by'];
            $order_direction    = $input['order'][0]['dir'];

            if($order_column_name!=''){
                $qb = $qb->orderBy($order_column_name, $order_direction);
            }
            
            //limit $ offset
            if((int)$input['start']!= 0 ){
                $qb = $qb->limit($input['length'])->offset($input['start']);
            }
            else{
                $qb = $qb->limit($input['length']);
            }

            $response = MobilePayment::arr_to_dt_response( 
                $qb->get(), $input['draw'],
                $total_records,
                $records_filtered
                );
        }
        else{
            $response = $qb->get();
        }

        return response()->json($response, 200);
    }


















    /**
     * Operation uploadBankFile
     *
     * Upload bank file for reconciliation.
     *
     * @return Http response
     */
    public function uploadBankFile()
    {
        try{
            $payment_refs = [];
            $mobile_payment_ids = [];
            $form = Request::only('file');
            $file = $form['file'];

            $handle = fopen($file, 'r');
            $header = true;
            while($csvLine = fgetcsv($handle, 1000, ',')){
                if ($header) {
                    $header = false;
                } else {
                    $ref = explode(" ", $csvLine[1])[0];
                    array_push($mobile_payment_ids, (int)preg_replace("/[^0-9]/", "", $ref)); 
                }
            }
            // Get only unique values
            $mobile_payment_ids = array_unique($mobile_payment_ids);            

            // Change status of the mobile payment(s) to paid
            foreach($mobile_payment_ids as $id){
                $mobile_payment = MobilePayment::findOrFail($id);
                $mobile_payment->status_id = 5;
                $mobile_payment->disableLogging();
                $mobile_payment->save();
                $payment_refs[] = $mobile_payment->ref;
                
                activity()                              // Logging payment
                   ->performedOn($mobile_payment)
                   ->causedBy($this->current_user())
                   ->log('Paid');
            }

            return Response()->json(array('msg' => 'Success: Mobile Payment(s) reconciled','payments' => $payment_refs), 200);
        }
        catch (Exception $e){
            return response()->json(['error'=>$e->getMessage()], 500);
        }
    }


