            $qb->where(function ($query) use ($input) {
                
                $query->orWhere('staff.id','like', '\'%' . $input['search']['value']. '%\'');
                $query->orWhere('staff.staff_name','like', '\'%' . $input['search']['value']. '%\'');
                $query->orWhere('staff.staff_desc','like', '\'%' . $input['search']['value']. '%\'');

